# Caddyfile for HomeLab services (macOS)

# Global options
{
    # Use self-signed certificates for local development
    local_certs
}

# Main domain redirect
{{domain}} {
    redir https://portainer.{{domain}}
}

# Core Services
portainer.{{domain}} {
    reverse_proxy portainer:9000
}

duckdb.{{domain}} {
    reverse_proxy duckdb:80
}

files.{{domain}} {
    reverse_proxy copyparty:3923
}

# Optional Services
n8n.{{domain}} {
    reverse_proxy n8n:5678
}

kestra.{{domain}} {
    reverse_proxy kestra:8080
}

keystonejs.{{domain}} {
    reverse_proxy keystonejs:3000
}

minio.{{domain}} {
    reverse_proxy minio:9001
}

ollama.{{domain}} {
    reverse_proxy ollama:11434
}

cockpit.{{domain}} {
    reverse_proxy cockpit:80
}

authelia.{{domain}} {
    reverse_proxy authelia:9091
}

rabbitmq.{{domain}} {
    reverse_proxy rabbitmq:15672
}

grafana.{{domain}} {
    reverse_proxy grafana:3000
}

loki.{{domain}} {
    reverse_proxy loki:3100
}

trivy.{{domain}} {
    reverse_proxy trivy:8080
}

sonarqube.{{domain}} {
    reverse_proxy sonarqube:9000
}

nexus.{{domain}} {
    reverse_proxy nexus:8081
}

vault.{{domain}} {
    reverse_proxy vault:8200
}

rapidoc.{{domain}} {
    reverse_proxy rapidoc:80
}

psitransfer.{{domain}} {
    reverse_proxy psitransfer:3005
}

excalidraw.{{domain}} {
    reverse_proxy excalidraw:80
}

drawio.{{domain}} {
    reverse_proxy drawio:8088
}

kroki.{{domain}} {
    reverse_proxy kroki:8000
}

outline.{{domain}} {
    reverse_proxy outline:3000
}

grist.{{domain}} {
    reverse_proxy grist:8484
}

nocodb.{{domain}} {
    reverse_proxy nocodb:8080
}

jasper.{{domain}} {
    reverse_proxy jasperreports:8080
}

onedev.{{domain}} {
    reverse_proxy onedev:6610
}

registry.{{domain}} {
    reverse_proxy registry:5000
}

localstack.{{domain}} {
    reverse_proxy localstack:4566
}

docuseal.{{domain}} {
    reverse_proxy docuseal:3000
}

translate.{{domain}} {
    reverse_proxy libretranslate:5000
}
