content:
# Caddyfile for HomeLab services

# Main domain redirect
{{domain}} {
    redir https://portainer.{{domain}}
}

# Portainer
portainer.{{domain}} {
    reverse_proxy portainer:9000
}

# File sharing
files.{{domain}} {
    reverse_proxy copyparty:3923
}

# n8n (if enabled)
n8n.{{domain}} {
    reverse_proxy n8n:5678
}

# Global options
{
    # Enable automatic HTTPS
    auto_https on

    # Email for Let's Encrypt
    email admin@{{domain}}
}
